
public class jiecheng {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
        long sum=0;
        long t=1;
        for(int i=1;i<21;i++){
        	t=t*i;
        	sum=sum+t;
        	
        }
        System.out.println("前二十项的和为:"+sum);
	}

}
